import { MonoExplore, MonoFiberNew } from '@kadena/react-icons/system';
import type { Meta, StoryFn, StoryObj } from '@storybook/react';
import React from 'react';
import { withCenteredStory, withContentWidth } from '../../storyDecorators';
import type { IAvatarProps } from './Avatar';
import { Avatar } from './Avatar';

const meta: Meta<IAvatarProps> = {
  title: 'Components/Avatar',
  decorators: [withContentWidth, withCenteredStory],
  component: Avatar,
  parameters: {
    status: {
      type: ['stable'],
    },
    docs: {
      description: {
        component:
          'A component used for displaying user icon with a specific background color and an optional status circle.',
      },
    },
  },
  argTypes: {
    size: {
      control: {
        type: 'select',
      },
      options: ['sm', 'md', 'lg'],
    },
  },
};

export default meta;
type Story = StoryObj<IAvatarProps>;

export const WithName: Story = {
  name: 'Avatar WithName',
  args: {
    name: 'Mafi Rulis',
    status: undefined,
  },
  render: ({ name, imageUrl, color, size, status, icon }) => {
    return (
      <Avatar
        name={name}
        color={color}
        imageUrl={imageUrl}
        size={size}
        status={status}
        icon={icon}
      />
    );
  },
};

